mapper.select:
select rp.staffCode, dp.姓名 as staffName, dp.岗位 as staffJobs, bb.id as bookID, bb.clockBook, rp.clockDate, rd.clockDay, rd.clockDirectory, rd.audioURL, rd.videoURL, REPLACE(REPLACE(content,CHAR(10),'\n'),CHAR(13),'\n') content, dp.部门 as department, rp.actualReading, rp.ReadingFeeling, rp.actualReading, rp.num, sg.struggleName, sg.struggleId from Reading_plancontent rp left join Reading_detail rd on rp.ChapterId=rd.id left join books bb on rd.BookId=bb.id left join datacenter_personnel dp on rp.staffCode=dp.人员编码 LEFT JOIN struggle sg ON rp.staffCode=sg.staffCode where rp.staffCode=#{staffCode}
and rp.clockDate=#{clockDate}
and clockDate >= #{beginClockDate} and clockDate<=#{endClockDate}
and rd.clockDirectory=#{clockDirectory}

mapper.update:
UPDATE
Reading_plancontent
SET actualReading =
now(),timeLength=#{timeLength},ReadingFeeling=#{feeling},num=#{num}
WHERE staffCode=#{staffCode}
and clockDate=#{clockDate}
